import React from 'react';
import {Gitgraph, templateExtend, TemplateName} from '@gitgraph/react';
import {createFixedHashGenerator} from './helpers';

export function ResumeGraphComponent() {
    const withoutAuthor = templateExtend(TemplateName.Metro, {
        commit: {
            message: {
                displayAuthor: false,
                displayHash: false
            },
        },
    });

    return (
        <Gitgraph options={{
            template: withoutAuthor
            // generateCommitHash: createFixedHashGenerator()
        }}>
            {(gitgraph) => {
                // Simulate git commands with Gitgraph API.
                const master = gitgraph.branch("Srinivas Gannu");
                master.commit("Initial commit");
                master.merge(education(gitgraph), "<br>Built C compiler - Lexical analysis, Syntax analysis (lex & yacc)").tag("B'lor Degree");
                const work = gitgraph.branch("Professional experience");
                work.commit("Bought some business casuals.");
                work.merge(alcatel(gitgraph), "few french words: Bonjour, c'est mon premier boulot.").tag("Developer - Entry level");
                work.merge(amdocs(gitgraph), "Europe & US culture, enjoyed travelling & food.").tag("Sr. Subject Matter Expert");
                master.merge(work, "Customer statisfaction, production scale").tag(" Matter Expert");
            }}
        </Gitgraph>
    );
}

function education(gitgraph) {
    const edu = gitgraph.branch("Computer Science");
    edu.commit("Mathematics, Algos & DS, DB Systems, Programming, Networking");
    edu.commit("Information Systems, Computation & Automata etc");
    return edu;
}

function alcatel(gitgraph) {
    const branch = gitgraph.branch("Alcatel India Ltd");
    branch.commit("Extreme programming, experiments and fun driven development");
    branch.commit("built an application layer for CAMEL protocol for SS7 stack on TCP");
    return branch;
}

function amdocs(gitgraph) {
    const branch = gitgraph.branch("Amdocs Ltd.");
    branch.commit("Extreme programming, linux kernel, IPC/socket programming, multi threading, shared memory etc");
    branch.commit("From C, C++ to Java & web technologies");
    branch.commit("multi threading, web services, unit testing etc");
    branch.commit("Front end applications, desktop tools, automate tidious tasks, pl/sql");
    branch.commit("Reusable modules/libraries, apis");
    return branch;
}
