import React from 'react';
import {Gitgraph, templateExtend, TemplateName} from '@gitgraph/react';
import {createFixedHashGenerator} from './helpers';

export function ResumeGraphComponent() {
    const withoutAuthor = templateExtend(TemplateName.Metro, {
        commit: {
            message: {
                displayAuthor: false,
            },
        },
    });


    return (
        <Gitgraph options={{
            template: withoutAuthor,
            generateCommitHash: createFixedHashGenerator()
        }}>
            {(gitgraph) => {
                // Simulate git commands with Gitgraph API.
                const master = gitgraph.branch("Srinivas Gannu");
                master.commit("Initial commit");
                master.merge(education(gitgraph), "Built C compiler - Lexical analysis, Syntax analysis (lex & yacc)").tag("2004");
                master.merge(alcatel(gitgraph), "from Student to Employee").tag("Jun 2004 - Jun 2005");
                master.merge(amd(gitgraph), "from Student to Employee").tag("Jun 2004 - Jun 2005");
            }}
        </Gitgraph>
    );
}

function education(gitgraph) {
    const edu = gitgraph.branch("Education");
    edu.commit("Mathematics, Algos & DS, DB Systems, Programming, Networking");
    edu.commit("Information Systems, Computation & Automata etc");
    return edu;
}

function alcatel(gitgraph) {
    const edu = gitgraph.branch("Alcatel India Ltd");
    edu.commit("Extreme programming");
    edu.commit("Built application layer for CAMEL protocol for SS7 stack on TCP");
    edu.commit("Bonjour, c'est mon premier boulot, learnt few french words.")
    return edu;
}
